package org.mohang.service;

import java.io.File;
import java.io.FileInputStream;
import java.io.FileOutputStream;
import java.io.InputStream;
import java.io.OutputStream;
import java.util.List;

import javax.servlet.http.HttpServletResponse;

import org.apache.commons.codec.binary.Base64;
import org.apache.commons.io.FileUtils;
import org.apache.commons.io.FilenameUtils;
import org.apache.poi.ss.usermodel.ClientAnchor;
import org.apache.poi.ss.usermodel.CreationHelper;
import org.apache.poi.ss.usermodel.Drawing;
import org.apache.poi.ss.usermodel.Picture;
import org.apache.poi.util.IOUtils;
import org.apache.poi.xssf.streaming.SXSSFSheet;
import org.apache.poi.xssf.streaming.SXSSFWorkbook;
import org.mohang.domain.ExcelDTO;
import org.springframework.stereotype.Service;
import org.springframework.util.FileCopyUtils;
import org.springframework.web.bind.annotation.RequestParam;

import lombok.extern.log4j.Log4j;

@Log4j
@Service
public class ExcelServiceImpl implements ExcelService {

	private String filePath = "/Users/unknown1/Desktop/";

	private static final String BASE64_PNG_PRE_FIX = "data:image/png;base64,";
	private int rowCount;
	private int cellCount;

	@Override
	public void downloadExcel(HttpServletResponse response, List<ExcelDTO> excelUriList) throws Exception {
		// init
		SXSSFWorkbook workBook = new SXSSFWorkbook();
		workBook.setCompressTempFiles(true);
		SXSSFSheet sheet = workBook.createSheet("Hello sheet");
		sheet.setRandomAccessWindowSize(100);
		rowCount = 0;
		cellCount = 0; // data setting
		// this.setTitle(workBook, sheet, param);
		this.setChart(workBook, sheet, excelUriList);
		// this.setData(workBook, sheet, param);
		// file download
		String fullpath = "Test.xlsx";
		this.write(workBook, fullpath);
		this.downloadExcelFileToWeb(new File(fullpath), response);
	}

	@Override
	public void downloadExcel(HttpServletResponse response) throws Exception {
		// init
		SXSSFWorkbook workBook = new SXSSFWorkbook();
		workBook.setCompressTempFiles(true);
		SXSSFSheet sheet = workBook.createSheet("Hello sheet");
		sheet.setRandomAccessWindowSize(100);
		rowCount = 0;
		cellCount = 0; // data setting
		// this.setTitle(workBook, sheet, param);
		String fileName = "";
		this.setChart(workBook, sheet, fileName);
		// this.setData(workBook, sheet, param);
		// file download
		String fullpath = "Test.xlsx";
		this.write(workBook, fullpath);
		this.downloadExcelFileToWeb(new File(fullpath), response);
	}

	@Override
	public void setChart(SXSSFWorkbook workbook, SXSSFSheet sheet, String excelUriList) {

		InputStream in = null;
		FileOutputStream fos = null;
		File imageFile = null;
		String strList[] = new String[3];
		strList[0] = "data:image/png;base64,";
		strList[1] = "data:image/png;base64,";
		strList[2] = "data:image/png;base64,";
		for (int i = 0; i < strList.length; i++) {
			try {
				String encodingStr = strList[i].replace(BASE64_PNG_PRE_FIX, "");
				byte[] decodeImg = Base64.decodeBase64(encodingStr);
				FileUtils.forceMkdir(new File(filePath)); //    
				imageFile = new File(filePath + "/chartImg" + i +".png");
				fos = new FileOutputStream(imageFile);
				fos.write(decodeImg);
				in = new FileInputStream(imageFile);
				byte[] bytes = IOUtils.toByteArray(in);
				int pictureIdx = workbook.addPicture(bytes, SXSSFWorkbook.PICTURE_TYPE_PNG);
				final CreationHelper helper = workbook.getCreationHelper();
				final Drawing drawing = sheet.createDrawingPatriarch();
				final ClientAnchor anchor = helper.createClientAnchor();
				//   CELL  
				cellCount = 0;
				anchor.setCol1(cellCount++);
				anchor.setRow1(rowCount++); //  
				final Picture pict = drawing.createPicture(anchor, pictureIdx); //    
				pict.resize();
				rowCount = rowCount + 20; // FIXME   rowCount 
			} catch (Exception e) {
				e.printStackTrace();
				log.error(e.getMessage(), e);
			} finally {
				IOUtils.closeQuietly(in);
				IOUtils.closeQuietly(fos);
			}
		}
	}

	@Override
	public void setChart(SXSSFWorkbook workbook, SXSSFSheet sheet, List<ExcelDTO> excelUriList) {

		InputStream in = null;
		FileOutputStream fos = null;
		File imageFile = null;

		try {
			String encodingStr = excelUriList.get(0).getUrl().replace(BASE64_PNG_PRE_FIX, "");
			byte[] decodeImg = Base64.decodeBase64(encodingStr);
			FileUtils.forceMkdir(new File(filePath)); //    
			imageFile = new File(filePath + "/chartImg.png");
			fos = new FileOutputStream(imageFile);
			fos.write(decodeImg);
			in = new FileInputStream(imageFile);
			byte[] bytes = IOUtils.toByteArray(in);
			int pictureIdx = workbook.addPicture(bytes, SXSSFWorkbook.PICTURE_TYPE_PNG);
			final CreationHelper helper = workbook.getCreationHelper();
			final Drawing drawing = sheet.createDrawingPatriarch();
			final ClientAnchor anchor = helper.createClientAnchor();
			//   CELL  
			cellCount = 0;
			anchor.setCol1(cellCount++);
			anchor.setRow1(rowCount++); //  
			final Picture pict = drawing.createPicture(anchor, pictureIdx); //    
			pict.resize();
			rowCount = rowCount + 20; // FIXME   rowCount 
		} catch (Exception e) {
			e.printStackTrace();
			log.error(e.getMessage(), e);
		} finally {
			IOUtils.closeQuietly(in);
			IOUtils.closeQuietly(fos);
		}

	}

	private void write(SXSSFWorkbook workBook, String fullPath) throws Exception {
		// file write
		FileOutputStream fos = null;
		try {
			fos = new FileOutputStream(fullPath);
			workBook.write(fos);
		} finally {
			IOUtils.closeQuietly(fos);
			IOUtils.closeQuietly(workBook);
		}
	}

	private void downloadExcelFileToWeb(File excelFile, HttpServletResponse res) {
		if (excelFile == null || excelFile.exists() == false) {
			throw new IllegalArgumentException("invalid excelFile path");
		}
		String fileNm = FilenameUtils.getName(excelFile.getAbsolutePath());
		res.setContentType("applicaiton/vnd.ms-excel;charset=utf-8");
		res.setHeader("Content-Disposition", "attachment;filename=\"" + fileNm + "\";");
		OutputStream out = null;
		FileInputStream fis = null;
		try {
			out = res.getOutputStream();
			fis = new FileInputStream(excelFile);
			FileCopyUtils.copy(fis, out);
			// buffer copy
			out.flush();
		} catch (Exception e) {
			throw new RuntimeException(e.getMessage());
		} finally {
			IOUtils.closeQuietly(fis);
			IOUtils.closeQuietly(out);
			excelFile.delete();
		}
	}

}
