mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM as rnum FROM ( select a.ap_num, e.e_name, a.admin_num, a.ap_date, a.ap_check, e.e_num from approve a, event e, event_hall_reservation r where r.eh_reservation_num = a.eh_reservation_num and e.e_num = r.e_num
and e.e_name like '%'||#{keyword}||'%' order by to_number(admin_num) desc ) a)
where rnum > (#{pageNum} -1) * #{amount} and rnum <= #{pageNum} * #{amount}

mapper.update:
update approve
set AP_Check = 'Y'
where ap_num =#{ap_num} and admin_num=#{admin_num}

mapper.select:
select *
from event
where e_num = #{e_num}

mapper.update:
update approve
set AP_Check = 'N'
where ap_num = #{ap_num} and admin_num = #{admin_num}

update account 
set account_delete = 'N'
where account_num = #{account_num}

mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM as rnum FROM ( SELECT * FROM review
where review_title like '%'||#{keyword}||'%' order by to_number(review_num) desc
) a)
where rnum > (#{pageNum} -1) * #{amount} and rnum <= #{pageNum} * #{amount}

select notice_num, notice_title, notice_writer, notice_date, notice_hitcount , notice_content
from notice
where notice_num=#{notice_num}

mapper.update:
update notice
set notice_title=#{notice_title}, notice_content=#{notice_content} 
where notice_num = #{notice_num}

mapper.select:
SELECT * FROM ( SELECT a.*, ROWNUM as rnum FROM (
SELECT * FROM notice where notice_title like '%'||#{keyword}||'%' order by to_number(notice_num) desc
) a)
where rnum > (#{pageNum} -1) * #{amount} and rnum <= #{pageNum} * #{amount}
