mapper.select:
select e.e_num,e.o_num,e.e_name,e.e_startdate,e.e_enddate,e.e_fname,e.e_price,e.e_hitcount from event e, approve a,event_hall_reservation r where r.eh_reservation_num = a.eh_reservation_num and e.e_num = r.e_num and a.ap_check='Y' and
e.e_field = #{account_interest}
order by e.e_hitcount desc

select e_num as e_num,o_num as o_num,e_name as e_name,e_startdate as e_startDate,e_enddate as e_endDate,e_fname as e_fname,e_price as e_price,e_hitcount as e_hitcount from ( select a.*, rownum as rnum from ( select e.e_num as e_num,e.o_num as o_num,e.e_name as e_name,e.e_startdate as e_startDate,e.e_enddate as e_endDate,e.e_fname as e_fname,e.e_price as e_price,e.e_hitcount as e_hitcount from event e, approve a,event_hall_reservation r where r.eh_reservation_num = a.eh_reservation_num and e.e_num = r.e_num and a.ap_check='Y' and e_enddate > sysdate
and e.e_name like '%'||#{keyword}||'%'
and e.e_field = #{field}
and e.e_type = #{type}
and e.e_price = 0
and e.e_price >= 0
and e.e_price > 0
and (e.e_startdate >= #{startDate}
and e.e_enddate <= #{endDate})
order by e.e_like desc
order by e.e_price desc
order by e.e_hitcount desc
) a) WHERE rnum > (#{pageNum} -1) * #{amount} and rnum <= (#{pageNum} * #{amount})

select count(*) from event e, approve a,event_hall_reservation r where r.eh_reservation_num = a.eh_reservation_num and e.e_num = r.e_num and a.ap_check='Y' and e_enddate > sysdate
and e.e_name like '%'||#{keyword}||'%'
and e.e_field = #{field}
and e.e_type = #{type}
and e.e_price = 0
and e.e_price >= 0
and e.e_price > 0
and (e.e_startdate >= #{startDate}
and e.e_enddate <= #{endDate})

select
account_num,e_num,like_status from liked where account_num
=#{account_num} and e_num = #{e_num}

select
account_num,e_num,like_status from liked where account_num
=#{account_num} and e_num = #{e_num}

select * from event where
e_num = #{e_num}
order by e_hitcount desc
